import React from 'react'
import http from '../../services/httpService';
import {apiUrl} from '../../config.json';

class ReactUploadImage extends React.Component {

    state = {
       /*  data:{},
        image:[] */
        file:null,
        title:"",
        price:"",
        description:""
    }

            
    onFormSubmit = (e) =>{
        e.preventDefault();
        const formData = new FormData();
       
        
        formData.append('title', this.state.title);
        formData.append('price', this.state.price);
        formData.append('image', this.state.file);
        formData.append('description', this.state.description);

        const config = {
            headers: {
                'content-type': 'multipart/form-data'
            },
           
        };
        http.post( `${apiUrl}/images`,formData,config)
            .then((response) => {
                alert("התמונה נוספה בהצלחה");
            }).catch((error) => {
        });
    }
    onChange = (e) => {
        this.setState({file:e.target.files[0]});
      
    }

    

    render() {

        

        return (
          /*   <div className="row h5 ps-5 pe-5">
            <form onSubmit={this.onFormSubmit} className="form-control">
                <h4>העלאת תמונה למוצר</h4>
                <input className="form-control m-2" type="file"  name="myImage" onChange= {this.onChange} required/>
                
                <button type="submit" className="form-control">שלח</button>
            </form>

            </div> */
            <div>
                <form onSubmit={this.onFormSubmit}>
              <h1>File Upload</h1>
              <label>name</label>
                <input name="title" type="text" onChange={(e) => {this.setState({title: e.target.value})}} />
            <label>price</label>
            <input name="price" type="text" onChange={(e) => {this.setState({price: e.target.value})}} />
            <label>image</label>
                <input type="file" onChange={this.onChange} />
                <label>description</label>
                <input name="description" type="text" onChange={(e) => {this.setState({description: e.target.value})}} />
              <button type="submit">Upload</button>
              </form>
            </div>
        )
    }
}

export default ReactUploadImage
