import React, { Component } from "react";
import Joi from "joi-browser";
import Input from "./input";

class Form extends Component {
  state = {
    data: {},
    errors: {}
  };

  validate = () => {
    const options = { abortEarly: false };
    const { error } = Joi.validate(this.state.data, this.schema, options);
    if (!error) return null;

    const errors = {};
    for (let item of error.details) errors[item.path[0]] = item.message;
    return errors;
  };

  validateProperty = ({ name, value }) =>{
    const obj = { [name]: value };
    const schema = { [name]: this.schema[name] };
    const { error } = Joi.validate(obj, schema);
    return error ? error.details[0].message : null;
  };

  handleSubmit = e => {
    e.preventDefault();

    const errors = this.validate();
    this.setState({ errors: errors || {} });
    if (errors) return;

    this.doSubmit();
  };

  handleChange = ({ currentTarget: input }) => {
    const errors = { ...this.state.errors };
    const errorMessage = this.validateProperty(input);
    if (errorMessage) errors[input.name] = errorMessage;
    else delete errors[input.name];

    const data = { ...this.state.data };
    data[input.name] = input.value;

    this.setState({ data, errors });
  };

  renderButton(label) {
    return (
      <button disabled={this.validate()} className="btn-lg my-btn">
        {label}
      </button>
    );
  }

  handleSelect = ({ currentTarget: select}) => {
    const data = { ...this.state.data };
    data[select.name] = select.value;
    this.setState({ data });

  }


  renderSelect(name, label, option){
    return (
      
      <div className="h5 ps-5 pe-5 text-secondary">
      <label htmlFor={name}>{label}</label>
      <select className="form-select" onChange={this.handleSelect}  name={name}><option>בחר</option>{option.map ( item=>( <option key={item}>{item}</option> ))}</select>
      </div>
    )
    
  }

  renderInput(name, label, type = "text", value, placeholder, ) {
    const { data, errors } = this.state;

    return (
      <Input
        type={type}
        name={name}
        value={data[name]}
        label={label}
        placeholder={placeholder}
        onChange={this.handleChange}
        error={errors[name]}
        
      />
    );
  }
}

export default Form;
