import React from 'react'
import http from '../../services/httpService';
import {apiUrl} from '../../config.json';

class ReactUploadImage extends React.Component {

    state ={
        catalogNumber: " "
    }
    
    
    
    onFormSubmit = (e) =>{
        e.preventDefault();
        this.onSetCatalogNumber() 
        const formData = new FormData();
        formData.append('image',this.state.file);
        const config = {
            headers: {
                'content-type': 'multipart/form-data'
            },
            body:{
                catalogNumber: this.state.catalogNumber
            },
        };
        http.post( `${apiUrl}/images`,formData,config)
            .then((response) => {
                alert("התמונה נוספה בהצלחה");
            }).catch((error) => {
        });
    }
    onChange = (e) => {
        this.setState({file:e.target.files[0]});
      
    }

    onSetCatalogNumber = (e) => {
        this.setState({value:e.target.value});
       
       
    }

    render() {



        return (
            <div className="row h5 ps-5 pe-5">
            <form onSubmit={this.onFormSubmit} className="form-control">
                <h4>העלאת תמונה למוצר</h4>
                <label>הכנס מספר קטלוגי של המוצר</label>
                    <input type="text" className="form-control" onChange={this.onSetCatalogNumber}></input>
                <input className="form-control m-2" type="file" value={this.state.catalogNumber
                } name="myImage" onChange= {this.onChange} required/>
                
                <button type="submit" className="form-control">שלח</button>
            </form>
            </div>
        )
    }
}

export default ReactUploadImage
