import React from 'react'
import http from '../../services/httpService';
import {apiUrl} from '../../config.json';

class ReactUploadImage extends React.Component {

 formRef = React.createRef();

    onSubmit = (e) => {
        e.preventDefault();
        const form = this.formRef.current
        const data = new FormData(form);
        http.post(`${apiUrl}/images`, data);
        
    }
        
    onFormSubmit = (e) =>{
        e.preventDefault();
        const formData = new FormData();
        formData.append('image',this.state.file);
        /* const {data}  = this.props;
        formData.append('data', data); */
        const config = {
            headers: {
                'content-type': 'multipart/form-data'
            },
           
        };
        http.post( `${apiUrl}/images`,formData,config)
            .then((response) => {
                alert("התמונה נוספה בהצלחה");
            }).catch((error) => {
        });
    }
    onChange = (e) => {
        this.setState({file:e.target.files[0]});
      
    }

    

    render() {

        

        return (
            /* <div className="row h5 ps-5 pe-5">
            <form onSubmit={this.onFormSubmit} className="form-control">
                <h4>העלאת תמונה למוצר</h4>
                <input className="form-control m-2" type="file"  name="myImage" onChange= {this.onChange} required/>
                
                <button type="submit" className="form-control">שלח</button>
            </form
            </div> */
            <div>
                <form ref={this.formRef}onSubmit={this.onSubmit} enctype="multipart/form-data" method="post" name="fileinfo">
  <label>Your email address:</label>
  <input type="email" autocomplete="on" autofocus name="userid" placeholder="email" required size="32" maxlength="64" /><br />
  <label>Custom file label:</label>
  <input type="text" name="filelabel" size="12" maxlength="32" /><br />
  <label>File to stash:</label>
  <input type="file" name="file" required />
  <input type="submit" value="Stash the file!" />
</form>

            </div>
        )
    }
}

export default ReactUploadImage
